import React, { useState } from "react";
import "./App.css";

function App() {
  const [displayValue, setDisplayValue] = useState("0");
  const [currentValue, setCurrentValue] = useState("");
  const [expression, setExpression] = useState([]);

  const handleNumberClick = (num) => {
    if (displayValue === "0" && num !== ".") {
      setDisplayValue(num.toString());
    } else if (num === "." && displayValue.includes(".")) {
      return; // Allow only one decimal point
    } else {
      setDisplayValue(displayValue + num.toString());
    }
    setCurrentValue(currentValue + num.toString());
  };

  const handleOperatorClick = (op) => {
    setCurrentValue("");
    setExpression([...expression, displayValue, op]);
  };

  const handleEqualClick = () => {
    if (currentValue !== "") {
      setExpression([...expression, displayValue]);
      const result = eval(expression.join('') + currentValue);
      setDisplayValue(result.toString());
      setCurrentValue("");
      setExpression([]);
    }
  };

  const handleClear = () => {
    setDisplayValue("0");
    setCurrentValue("");
    setExpression([]);
  };

  const handleDelete = () => {
    if (displayValue.length === 1 || displayValue === "-") {
      setDisplayValue("0");
      setCurrentValue("");
    } else {
      setDisplayValue(displayValue.slice(0, -1));
      setCurrentValue(currentValue.slice(0, -1));
    }
  };

  return (
    <div className="App">
      <div className="container_calculadora">
        <div className="pantalla">{displayValue}</div>
        <div className="buttons_calc">
          <button className="grey ac" onClick={handleClear}>AC</button>
          <button className="grey" onClick={() => handleNumberClick("(")}>(</button>
          <button className="grey">%</button>
          <button className="grey" onClick={() => handleOperatorClick("/")}>÷</button>
          <button onClick={() => handleNumberClick("7")}>7</button>
          <button onClick={() => handleNumberClick("8")}>8</button>
          <button onClick={() => handleNumberClick("9")}>9</button>
          <button className="grey" onClick={() => handleOperatorClick("*")}>X</button>
          <button onClick={() => handleNumberClick("4")}>4</button>
          <button onClick={() => handleNumberClick("5")}>5</button>
          <button onClick={() => handleNumberClick("6")}>6</button>
          <button className="grey" onClick={() => handleOperatorClick("-")}>-</button>
          <button onClick={() => handleNumberClick("1")}>1</button>
          <button onClick={() => handleNumberClick("2")}>2</button>
          <button onClick={() => handleNumberClick("3")}>3</button>
          <button className="grey" onClick={() => handleOperatorClick("+")}>+</button>
          <button onClick={() => handleNumberClick("0")}>0</button>
          <button onClick={() => handleNumberClick(".")}>.</button>
          <button onClick={handleDelete}>⌫</button>
          <button className="grey" onClick={handleEqualClick}>=</button>
        </div>
      </div>
    </div>
  );
}

export default App;
